import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../features/authSlice';

export default function Header(){
  const auth = useSelector(s=>s.auth);
  const dispatch = useDispatch();
  const nav = useNavigate();

  const onLogout = () => { dispatch(logout()); nav('/login'); };

  return (
    <header className="header">
      <div className="logo">AttendanceApp</div>
      <nav>
        {!auth.user && <>
          <Link to="/login">Login</Link>
          <Link to="/register">Register</Link>
        </>}
        {auth.user && <>
          {auth.user.role === 'employee' && <Link to="/employee">Employee</Link>}
          {auth.user.role === 'manager' && <Link to="/manager">Manager</Link>}
          <span className="user-info">{auth.user.name} ({auth.user.employeeId})</span>
          <button className="btn" onClick={onLogout}>Logout</button>
        </>}
      </nav>
    </header>
  );
}
