import React, { useEffect, useState } from 'react';
import API from '../api/api';

export default function ManagerDashboard(){
  const [list, setList] = useState([]);
  const [filter, setFilter] = useState({});

  const fetchAllToday = async () => {
    try {
      const res = await API.get('/attendance/today-status');
      setList(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchAll = async () => {
    try {
      const res = await API.get('/attendance/all');
      setList(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(()=>{ fetchAllToday(); }, []);

  return (
    <div className="card">
      <h2>Manager Dashboard</h2>
      <div>
        <button className="btn" onClick={fetchAllToday}>Today's attendance</button>
        <button className="btn" onClick={fetchAll}>All records</button>
      </div>

      <table className="table">
        <thead><tr><th>Name</th><th>Employee ID</th><th>Status</th><th>Check-in</th><th>Check-out</th></tr></thead>
        <tbody>
          {list.length === 0 && <tr><td colSpan="5">No records</td></tr>}
          {list.map((r, idx) => (
            <tr key={idx}>
              <td>{r.name || r.userId?.name}</td>
              <td>{r.employeeId || r.userId?.employeeId}</td>
              <td>{r.status || (r.attendance && r.attendance.checkInTime ? 'present' : 'absent')}</td>
              <td>{r.attendance?.checkInTime ? new Date(r.attendance.checkInTime).toLocaleTimeString() : '-'}</td>
              <td>{r.attendance?.checkOutTime ? new Date(r.attendance.checkOutTime).toLocaleTimeString() : '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
