import React, { useEffect, useState } from "react";
import API from "../api/api";
import { useSelector } from "react-redux";
import "../styles/ManagerDashboard.css";  // <-- ADD THIS

export default function ManagerDashboard() {
  const auth = useSelector((s) => s.auth);
  const [todayList, setTodayList] = useState([]);
  const [allList, setAllList] = useState([]);
  const [showToday, setShowToday] = useState(true);

  // Load today's attendance
  const loadToday = async () => {
    try {
      const res = await API.get("/attendance/all-today");
      setTodayList(res.data);
    } catch (err) {
      console.log(err);
      alert("Failed to load today's attendance (404)");
    }
  };

  // Load all attendance
  const loadAll = async () => {
    try {
      const res = await API.get("/attendance/all");
      setAllList(res.data);
    } catch (err) {
      console.log(err);
      alert("Failed to load all attendance (404)");
    }
  };

  useEffect(() => {
    loadToday();
    loadAll();
  }, []);

  return (
    <div className="manager-container">

      <h2 className="title">Manager Dashboard</h2>
      <h3 className="welcome">Welcome, {auth?.user?.name}</h3>

      <div className="button-group">
        <button
          onClick={() => setShowToday(true)}
          className={showToday ? "btn active" : "btn"}
        >
          Todayâ€™s Report
        </button>

        <button
          onClick={() => setShowToday(false)}
          className={!showToday ? "btn active" : "btn"}
        >
          All Records
        </button>
      </div>

      <h3 className="section-title">
        {showToday ? "Today's Attendance" : "All Attendance Records"}
      </h3>

      <div className="table-wrapper">
        <table className="custom-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Employee ID</th>
              <th>Date</th>
              <th>Check-in</th>
              <th>Check-out</th>
              <th>Hours</th>
              <th>Status</th>
            </tr>
          </thead>

          <tbody>
            {(showToday ? todayList : allList).map((item) => (
            <tr key={item._id}>
            <td>{item.userId?.name}</td>
            <td>{item.userId?.employeeId}</td>
            <td>{item.date}</td>
            <td>{item.checkInTime || "-"}</td>
            <td>{item.checkOutTime || "-"}</td>
            <td>{item.totalHours || "-"}</td>
            <td>{item.status}</td>
        </tr>
    ))}
    </tbody>


        </table>
      </div>

    </div>
  );
}
