import React, { useEffect, useState } from "react";
import API from "../api/api";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import "../styles/ManagerDashboard.css";

export default function ManagerDashboard() {
  const auth = useSelector((s) => s.auth);
  const navigate = useNavigate();
  const [todayList, setTodayList] = useState([]);
  const [allList, setAllList] = useState([]);
  const [showToday, setShowToday] = useState(true);

  const loadToday = async () => {
    try {
      const res = await API.get("/attendance/all-today");
      setTodayList(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  const loadAll = async () => {
    try {
      const res = await API.get("/attendance/all");
      setAllList(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    loadToday();
    loadAll();
  }, []);

  const exportCSV = () => {
    const data = showToday ? todayList : allList;
    if (!data.length) return alert("No records to export");

    const headers = ["Name", "Employee ID", "Date", "Check-in", "Check-out", "Hours", "Status"];
    const rows = data.map(d => [
      d.name, d.employeeId, d.date, d.checkInTime, d.checkOutTime, d.totalHours, d.status
    ]);

    let csv = "data:text/csv;charset=utf-8," + [headers, ...rows].map(r => r.join(",")).join("\n");

    const link = document.createElement("a");
    link.href = encodeURI(csv);
    link.download = showToday ? "today_report.csv" : "all_records.csv";
    document.body.appendChild(link);
    link.click();
    link.remove();
  };

  return (
    <div className="manager-wrapper">

      {/* SIDEBAR */}
      <aside className="sidebar">
        <h2 className="logo">üìä Manager</h2>

        <nav className="menu">
          <button className={showToday ? "active" : ""} onClick={() => setShowToday(true)}>
            üìÖ Today‚Äôs Report
          </button>

          <button className={!showToday ? "active" : ""} onClick={() => setShowToday(false)}>
            üìÅ All Records
          </button>

          <button onClick={() => navigate("/manager/charts")}>
            üìà Visualization
          </button>

          <button className="export-btn" onClick={exportCSV}>
            üì§ Export CSV
          </button>
        </nav>
      </aside>

      {/* MAIN CONTENT */}
      <main className="main-content">

        <header className="page-header">
          <h1>Welcome, {auth?.user?.name}</h1>
          <span className="subtitle">Manager Dashboard</span>
        </header>

        <h2 className="table-title">
          {showToday ? "üìÖ Today‚Äôs Attendance" : "üìÅ All Attendance Records"}
        </h2>

        <div className="table-container">
          <table className="styled-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Employee ID</th>
                <th>Date</th>
                <th>Check-in</th>
                <th>Check-out</th>
                <th>Hours</th>
                <th>Status</th>
              </tr>
            </thead>

            <tbody>
              {(showToday ? todayList : allList).map((item) => (
                <tr key={item._id}>
                  <td>{item.name}</td>
                  <td>{item.employeeId}</td>
                  <td>{item.date}</td>
                  <td>{item.checkInTime || "-"}</td>
                  <td>{item.checkOutTime || "-"}</td>
                  <td>{item.totalHours || "-"}</td>
                  <td className={item.status === "present" ? "present" : "absent"}>
                    {item.status}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

      </main>
    </div>
  );
}
