import React, { useEffect, useState } from "react";
import API from "../api/api";
import { useSelector } from "react-redux";

export default function EmployeeDashboard() {
  const auth = useSelector((state) => state.auth);
  const userId = auth?.user?.id;   // <-- IMPORTANT
  const [todayStatus, setTodayStatus] = useState(null);
  const [history, setHistory] = useState([]);

  // Fetch today status
  const loadStatus = async () => {
    if (!userId) return alert("User ID missing");
    const res = await API.get(`/attendance/today?userId=${userId}`);
    setTodayStatus(res.data);
  };

  // Fetch history
  const loadHistory = async () => {
    if (!userId) return alert("User ID missing");
    const res = await API.get(`/attendance/my-history?userId=${userId}`);
    setHistory(res.data);
  };

  // Check-In
  const handleCheckIn = async () => {
    if (!userId) return alert("User ID missing");
    await API.post("/attendance/checkin", { userId });
    loadStatus();
    loadHistory();
  };

  // Check-Out
  const handleCheckOut = async () => {
    if (!userId) return alert("User ID missing");
    await API.post("/attendance/checkout", { userId });
    loadStatus();
    loadHistory();
  };

  useEffect(() => {
    loadStatus();
    loadHistory();
  }, [userId]);

  return (
    <div className="container">
      <h2>Employee Dashboard</h2>
      <h3>Welcome, {auth?.user?.name}</h3>

      <div className="card">
        <h4>Today's Status</h4>
        <p>Status: {todayStatus?.status || "Loading..."}</p>

        <button onClick={handleCheckIn} className="btn btn-primary">
          Check In
        </button>
        <button onClick={handleCheckOut} className="btn btn-secondary">
          Check Out
        </button>
      </div>

      <h3>My Attendance History</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Hours</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {history.length === 0 ? (
            <tr>
              <td colSpan="5">No records</td>
            </tr>
          ) : (
            history.map((item) => (
              <tr key={item._id}>
                <td>{item.date}</td>
                <td>{item.checkInTime || "-"}</td>
                <td>{item.checkOutTime || "-"}</td>
                <td>{item.totalHours}</td>
                <td>{item.status}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
