import React, { useEffect, useState } from "react";
import API from "../api/api";
import "./Dashboard.css";

export default function ManagerDashboard() {
  const [todayList, setTodayList] = useState([]);
  const [allRecords, setAllRecords] = useState([]);

  const loadToday = async () => {
    const res = await API.get("/attendance/today-all");
    setTodayList(res.data);
  };

  const loadAll = async () => {
    const res = await API.get("/attendance/all");
    setAllRecords(res.data);
  };

  useEffect(() => {
    loadToday();
    loadAll();
  }, []);

  const formatTime = (t) => {
    if (!t) return "-";
    const d = new Date(t);
    return d.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
  };

  return (
    <div className="dash-container">
      <h2 className="dash-title">Manager Dashboard</h2>
      <h3 className="dash-subtitle">Today's Attendance Overview</h3>

      {/* TODAY TABLE */}
      <table className="styled-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Employee ID</th>
            <th>Date</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Hours</th>
            <th>Status</th>
          </tr>
        </thead>

        <tbody>
          {todayList.length === 0 ? (
            <tr>
              <td colSpan="7">No Data</td>
            </tr>
          ) : (
            todayList.map((item, i) => (
              <tr key={i}>
                <td>{item.name}</td>
                <td>{item.employeeId}</td>
                <td>{item.date}</td>
                <td>{formatTime(item.checkInTime)}</td>
                <td>{formatTime(item.checkOutTime)}</td>
                <td>{item.totalHours || "-"}</td>
                <td>
                  <span className={item.status === "present" ? "green" : "red"}>
                    {item.status}
                  </span>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      <h3 className="dash-subtitle">All Attendance Records</h3>

      {/* ALL RECORDS TABLE */}
      <table className="styled-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Employee ID</th>
            <th>Date</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Hours</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {allRecords.length === 0 ? (
            <tr>
              <td colSpan="7">No Records Found</td>
            </tr>
          ) : (
            allRecords.map((r, i) => (
              <tr key={i}>
                <td>{r.name}</td>
                <td>{r.employeeId}</td>
                <td>{r.date}</td>
                <td>{formatTime(r.checkInTime)}</td>
                <td>{formatTime(r.checkOutTime)}</td>
                <td>{r.totalHours}</td>
                <td>
                  <span className={r.status === "present" ? "green" : "red"}>
                    {r.status}
                  </span>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
