import React, { useEffect, useState } from "react";
import API from "../api/api";
import { useSelector } from "react-redux";

export default function ManagerDashboard() {
  const auth = useSelector((state) => state.auth);
  const [records, setRecords] = useState([]);
  const [mode, setMode] = useState("today"); // today | all

  // Fetch Today's Status of All Employees
  const loadToday = async () => {
    const res = await API.get("/attendance/all-today");
    setRecords(res.data);
    setMode("today");
  };

  // Fetch All Attendance Records
  const loadAll = async () => {
    const res = await API.get("/attendance/all");
    setRecords(res.data);
    setMode("all");
  };

  useEffect(() => {
    loadToday(); // default on page load
  }, []);

  return (
    <div className="container">
      <h2>Manager Dashboard</h2>
      <h3>Welcome, {auth?.user?.name}</h3>

      <div style={{ marginBottom: "20px" }}>
        <button onClick={loadToday} className="btn btn-primary">
          Todayâ€™s Report
        </button>

        <button onClick={loadAll} className="btn btn-secondary" style={{ marginLeft: "10px" }}>
          All Records
        </button>
      </div>

      <h3>
        {mode === "today" ? "Today's Attendance Status" : "All Attendance Records"}
      </h3>

      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Employee ID</th>
            <th>Date</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Hours</th>
            <th>Status</th>
          </tr>
        </thead>

        <tbody>
          {records.length === 0 ? (
            <tr>
              <td colSpan="7">No data available</td>
            </tr>
          ) : (
            records.map((r) => (
              <tr key={r._id || r.employeeId}>
                <td>{r.name}</td>
                <td>{r.employeeId}</td>
                <td>{r.attendance?.date || "-"}</td>
                <td>{r.attendance?.checkInTime || "-"}</td>
                <td>{r.attendance?.checkOutTime || "-"}</td>
                <td>{r.attendance?.totalHours || "-"}</td>
                <td>{r.status}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
