import React, { useEffect, useState } from "react";
import API from "../api/api";
import { useSelector } from "react-redux";

export default function EmployeeDashboard() {
  const auth = useSelector((state) => state.auth);
  const userId = auth?.user?.id;

  const [todayStatus, setTodayStatus] = useState(null);
  const [history, setHistory] = useState([]);

  // Convert ISO timestamp to readable format
  const formatTime = (time) => {
    if (!time) return "-";
    return new Date(time).toLocaleTimeString([], {
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  // Convert date string to readable format
  const formatDate = (date) => {
    if (!date) return "-";
    return new Date(date).toLocaleDateString();
  };

  // Load Today's Status
  const loadStatus = async () => {
    try {
      if (!userId) return alert("User ID missing");
      const res = await API.get(`/attendance/today?userId=${userId}`);
      setTodayStatus(res.data);
    } catch (err) {
      alert(err.response?.data?.message || "Failed to load status");
    }
  };

  // Load Full History
  const loadHistory = async () => {
    try {
      if (!userId) return alert("User ID missing");
      const res = await API.get(`/attendance/my-history?userId=${userId}`);
      setHistory(res.data);
    } catch (err) {
      alert(err.response?.data?.message || "Failed to load history");
    }
  };

  // Handle Check-In
  const handleCheckIn = async () => {
    try {
      if (!userId) return alert("User ID missing");

      const res = await API.post("/attendance/checkin", { userId });

      alert(res.data.message || "Checked in successfully");

      loadStatus();
      loadHistory();
    } catch (err) {
      alert(
        err.response?.data?.message ||
          "Check-in failed. Please try again."
      );
    }
  };

  // Handle Check-Out
  const handleCheckOut = async () => {
    try {
      if (!userId) return alert("User ID missing");

      const res = await API.post("/attendance/checkout", { userId });

      alert(res.data.message || "Checked out successfully");

      loadStatus();
      loadHistory();
    } catch (err) {
      alert(
        err.response?.data?.message ||
          "Check-out failed. Please try again."
      );
    }
  };

  // Load on page load
  useEffect(() => {
    if (userId) {
      loadStatus();
      loadHistory();
    }
  }, [userId]);

  return (
    <div className="container">
      <h2>Employee Dashboard</h2>
      <h3>Welcome, {auth?.user?.name}</h3>

      {/* Today's Status */}
      <div className="card">
        <h4>Today's Status</h4>
        <p>Status: {todayStatus?.status || "Loading..."}</p>

        <button onClick={handleCheckIn} className="btn btn-primary">
          Check In
        </button>
        <button onClick={handleCheckOut} className="btn btn-secondary">
          Check Out
        </button>
      </div>

      {/* History Table */}
      <h3>My Attendance History</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Hours</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {history.length === 0 ? (
            <tr>
              <td colSpan="5" className="text-center">
                No records found
              </td>
            </tr>
          ) : (
            history.map((item) => (
              <tr key={item._id}>
                <td>{formatDate(item.date)}</td>
                <td>{formatTime(item.checkInTime)}</td>
                <td>{formatTime(item.checkOutTime)}</td>
                <td>{item.totalHours || "-"}</td>
                <td>{item.status}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
