import React, { useEffect, useState } from "react";
import API from "../api/api";
import { useSelector } from "react-redux";

export default function ManagerDashboard() {
  const auth = useSelector((s) => s.auth);
  const [todayList, setTodayList] = useState([]);
  const [allList, setAllList] = useState([]);
  const [showToday, setShowToday] = useState(true);

  // Load today's attendance
  const loadToday = async () => {
    try {
      const res = await API.get("/attendance/all-today");
      setTodayList(res.data);
    } catch (err) {
      alert("Failed to load today's attendance (404). Check your backend route.");
      console.log(err);
    }
  };

  // Load all attendance
  const loadAll = async () => {
    try {
      const res = await API.get("/attendance/all");
      setAllList(res.data);
    } catch (err) {
      alert("Failed to load all attendance (404). Check your backend route.");
      console.log(err);
    }
  };

  useEffect(() => {
    loadToday();
    loadAll();
  }, []);

  return (
    <div className="container">
      <h2>Manager Dashboard</h2>
      <h3>Welcome, {auth?.user?.name}</h3>

      <button onClick={() => setShowToday(true)}>Today's Report</button>
      <button onClick={() => setShowToday(false)}>All Records</button>

      {showToday ? (
        <h3>Today's Attendance</h3>
      ) : (
        <h3>All Attendance Records</h3>
      )}

      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Employee ID</th>
            <th>Date</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Hours</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {(showToday ? todayList : allList).map((item) => (
            <tr key={item._id}>
              <td>{item.userId?.name}</td>
              <td>{item.userId?.employeeId}</td>
              <td>{item.date}</td>
              <td>{item.checkInTime || "-"}</td>
              <td>{item.checkOutTime || "-"}</td>
              <td>{item.totalHours || "-"}</td>
              <td>{item.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
